        lineWeightNum *= scale;
        lineWeightMod *= scale;
    }

    let lineNumX = LINE_ID_MIN_X + (LINE_ID_MAX_WIDTH - lineWeightTot) / 2;
    let lineNumW = lineWeightNum;

    let lineModX = lineNumX + lineNumW + 5;
    let lineModW = lineWeightMod;
