<script lang="ts" context="module">
    import type { StopLine } from "src/js/dataLayer";
    import SvgA from "./SvgA.svelte";

    import TileBlank from "./TileBlank.svelte";
</script>

<script lang="ts">
    export let line: StopLine;

    // Line ID layout calculations.
    const LINE_ID_MIN_X = 20;
    const LINE_ID_MAX_WIDTH = 480;

    let lineNumX: number, lineNumW: number, lineModX: number, lineModW: number;
    let hasIcon = false;
    $: {
        let lineWeightNum = 220 * line.lineNum.length;
        let lineWeightMod = 133 * line.lineMod.length;
        let lineWeightTot = lineWeightNum + 7 + lineWeightMod;

        const scale = Math.tanh(LINE_ID_MAX_WIDTH / lineWeightTot);
        lineWeightTot *= scale;
        lineWeightNum *= scale;
        lineWeightMod *= scale;

        lineNumX = LINE_ID_MIN_X + (LINE_ID_MAX_WIDTH - lineWeightTot) / 2;
        lineNumW = lineWeightNum;

        lineModX = lineNumX + lineNumW + 7;
        lineModW = lineWeightMod;

        hasIcon = line.isOwl || line.isCableCar || line.isHistoricStreetcar;
    }

    let nameLimit: null | number = null;
    let nameNarrow = false;
    $: {
        if (hasIcon) {
            if (17 <= line.lineName.length) {
                nameLimit = 850;
                nameNarrow = 20 <= line.lineName.length;
            }
        } else {
            if (23 <= line.lineName.length) {
                nameLimit = 1020;
                nameNarrow = 26 <= line.lineName.length;
            }
        }
    }

    let dest0Limit: null | number = null;
    let dest1Limit: null | number = null;
    $: {
        if (hasIcon) {
            if (28 <= line.lineDest0.length) {
                dest0Limit = 820;
            }
            if (28 <= line.lineDest1.length) {
                dest1Limit = 820;
            }
        } else {
            if (36 <= line.lineDest0.length) {
                dest0Limit = 1020;
            }
            if (36 <= line.lineDest1.length) {
                dest1Limit = 1020;
            }
        }
    }

    let timeLimit = null;
    $: {
        if (hasIcon) {
            if (31 <= line.lineTime.length) {
                timeLimit = 860;
            }
        } else {
            if (36 <= line.lineTime.length) {
                timeLimit = 1020;
            }
        }
    }
</script>

<TileBlank lineColor={line.lineColor} />
<SvgA href="https://www.sfmta.com/{line.lineId}">
    {#if line.isMetro}
        <rect
            x={lineNumX - 37.5}
            y="37.5"
            width={lineNumW + 90}
            height="300"
            rx="150"
            fill={line.lineTextColor}
        />
    {/if}
    <text
        class="line-number"
        class:metro={line.isMetro}
        x={lineNumX}
        y={line.isMetro ? 292.5 : 332.5}
        fill={line.isMetro ? line.lineColor : line.lineTextColor}
        textLength={lineNumW}
        lengthAdjust="spacingAndGlyphs">{line.lineNum}</text
    >
    {#if line.lineMod}
        <text
            class="line-modifier"
            x={lineModX}
            y="231"
            fill={line.lineTextColor}
            textLength={lineModW}
            lengthAdjust="spacingAndGlyphs">{line.lineMod}</text
        >
    {/if}
    <text
        class="line-name"
        class:narrow={nameNarrow}
        x="530"
        y="95"
        fill={line.lineTextColor}
        textLength={nameLimit}
        lengthAdjust="spacingAndGlyphs">{line.lineName?.toUpperCase()}</text
    >
</SvgA>
